  `attr_id` int(11) NOT NULL AUTO_INCREMENT,
  `attr_name` varchar(32) NOT NULL,
  `attr_pid` int(11) NOT NULL,
  PRIMARY KEY (`attr_id`)
) ENGINE=InnoDB  DEFAULT CHARSET=utf8 AUTO_INCREMENT=10 ;

